package Chapter3;

public class OpEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(10 % 8); 	// 2
		System.out.println(-10 % 8);	//-2
		System.out.println(10 % -8);	// 2
		System.out.println(-10 % -8);	//-2

		System.out.println(10 / 8);		// 1
		System.out.println(-10 / 8);	//-1
		System.out.println(10 / -8); 	//-1
		System.out.println(-10 / -8);	// 1 (음수)/(음수) = 양수
	}

}
